DROP TABLE import;

CREATE TABLE import (
  id serial primary key,
  title character varying(255) NOT NULL UNIQUE,
  author text NOT NULL,
  description text,
  isbn13 character varying(13) NOT NULL UNIQUE,
  category character varying(255) NOT NULL,

  imported_title character varying(255),
  imported_author character varying(255),
  imported_publishedDate character varying(255),
  imported_description text,
  imported_pageCount character varying(255),
  imported_category character varying(255),
  imported_language character varying(255),
  imported_isbn10 character varying(255),
  imported_isbn13 character varying(255),

  imported boolean DEFAULT false
);

